query NetworkTraffic($name: String!, $timeRange: Int!, $timeRangeUnit: TimeRangeUnit!) {
  metric(name: $name, timeRange: $timeRange, timeRangeUnit: $timeRangeUnit) {
    status
    ...MetricParts
  }
}

fragment MetricParts on TimeSeriesQueryResult {
  data {
    result {
      metric
      values
    }
  }
}
